import Profile from "./Profile.jsx";
import drumfill from "../sounds/drumfill.mp3";
import Card from 'react-bootstrap/Card';
import ListGroup from 'react-bootstrap/ListGroup';

function AllProfiles(props) {
  const drumFillAudio = new Audio(drumfill);
  drumFillAudio.volume = 0.02;
  const { users } = props;

  function alterDate(UTCString) {
    return Date(UTCString).split(' ').slice(0,3).join(' ')
  }
  return (
    <div>
      <a href="top" name="top">
        <h2>Profiles:</h2>
      </a>

      <Card style={{ width: '18rem' }}>
      <Card.Body>
        <Card.Title>Card Title</Card.Title>
        <Card.Subtitle className="mb-2 text-muted">Card Subtitle</Card.Subtitle>
        <Card.Text>
          Some quick example text to build on the card title and make up the
          bulk of the card's content.
        </Card.Text>
        <Card.Link href="#">Card Link</Card.Link>
        <Card.Link href="#">Another Link</Card.Link>
      </Card.Body>
      </Card>

      <div id="all-profiles-parent-div">

        {users.map((item, key) => {

        //  item stores data of musician's profile
          return (

            <Card style={{ width: '18rem', backgroundColor: 'black',boxShadow: '1px 1px 1px 1px white' }}>
            <Card.Body>
                <Card.Title>{item.name}</Card.Title>
                <Card.Subtitle className="mb-2 text-muted">{item.instrument_name}</Card.Subtitle>
                <img alt="user icon" src={item.picture_url} />

                <ListGroup  variant="flush">
        <ListGroup.Item style={{backgroundColor: 'black', color: 'white',border:'white'}}>Cras justo odio</ListGroup.Item>
        <ListGroup.Item>Dapibus ac facilisis in</ListGroup.Item>
        <ListGroup.Item>Vestibulum at eros</ListGroup.Item>
                </ListGroup>

                <Card.Text>
                  email: {item.email_address}
                </Card.Text>
                <Card.Text>
                genre: {item.genre_name}
                </Card.Text>
                <Card.Text>
                <p>joined: {alterDate(item.created_at)}</p>
                </Card.Text>
                <Card.Text>
                <p>location: {item.location_name}</p>
                </Card.Text>
                <Card.Text>
                <p>instrument: {item.instrument_name}</p>
                </Card.Text>
                <Card.Text>
                <p>looking for: {item.looking_for}</p>
                </Card.Text>

            </Card.Body>
            </Card>
        );
        })}
      </div>
      {/* <a href="#top">
        <button
          onClick={function () {
            drumFillAudio.play();
          }}
        >
          Back to Top
        </button>
      </a> */}
      </div>
      // </div>
  );
}

export default AllProfiles;
